     stack_title  : false,
     title_below_image : true
  },

   data : [
    {
       filter :  Brainstorming ,
       keyword : {
         color :  #d21559 
      }
    },
    {
       filter :  Organizing ,
       keyword : {
         color :  #feb909 
      }
    },
