def verify_option(value)
  case value
  when String
    @polymorphic_option = value
  when Array
    @polymorphic_option = value.join(   )
  when Hash
    @polymorphic_option = value.to_s
  else
    UI.user_error!  Invalid option: #{value.inspect} 
  end
end
